        const token = x.token;
        if (rememberMe) {
          localStorage.setItem('authToken', token);
          localStorage.setItem('rememberMe', 'true');
        } else {
          sessionStorage.setItem('authToken', token);
          localStorage.setItem('rememberMe', 'false');
        }
        this.router.navigate(['/PAGE']);
