import unittest
from flask import Flask
from ..services.phoneSwapService import PhoneSwapService, validate_phone_swap_request

class TestPhoneSwapService(unittest.TestCase):
    def setUp(self):
        self.app = Flask(__name__)
        self.service = PhoneSwapService(None, None)

    def test_validate_phone_swap_request_valid(self):
        data = {
            "requestedPhoneId": "1",
            "offeredPhoneId": "2"
        }
        self.assertTrue(validate_phone_swap_request(data))

    def test_validate_phone_swap_request_missing_fields(self):
        data = {
            "requestedPhoneId": "1"
        }
        self.assertFalse(validate_phone_swap_request(data))

