    this.newPhone = {
      brand: this.phoneForm.value.brand,
      model: this.phoneForm.value.model,
      storage: this.phoneForm.value.storage,
      price: this.phoneForm.value.price,
      id: this.phoneForm.value.id
    };

  }

}

