      for (const i in this.phoneForm.controls) {
        if (this.phoneForm.controls.hasOwnProperty(i)) {
          this.phoneForm.controls[i].markAsDirty();
          this.phoneForm.controls[i].updateValueAndValidity();
        }
      }
      return;
    }

