        // Parse hunk header: @@ -old_start,old_count +new_start,new_count @@
        const match = line.match(/@@ -(\d+)(?:,(\d+))? \+(\d+)(?:,(\d+))? @@/)
        if (match) {
          oldLineNum = parseInt(match[1]) - 1
          newLineNum = parseInt(match[3]) - 1
