export class AuthenticationService {
  private apiUrl = 'https://localhost:7214/Users';

  constructor (private http: HttpClient, private router: Router) { }

  readonly httpOptions = {
    headers: new HttpHeaders({
      'Content-Type':  'application/json',
    })
  }

  serviceCall() {
    console.log("Service was called");
  }


  login(user: any): Observable<any> {
    return this.http.post(`${this.apiUrl}/login`, JSON.stringify(user), this.httpOptions)
  }

  register(user: any): Observable<any> {
    return this.http.post(`${this.apiUrl}/register`, JSON.stringify(user), this.httpOptions);
  }

  logout(): void {
    // remove user token from storage to log user out
    localStorage.removeItem('authToken');
    sessionStorage.removeItem('authToken');
    localStorage.setItem('rememberMe', 'false');
    this.router.navigateByUrl('/login');
  }

}

    public class UserService : IUserService
    {
        private readonly IMongoCollection<UserDto> _users;
        public UserService(IMongoDBSettings settings)
        {
            var client = new MongoClient(settings.ConnectionString);
            var database = client.GetDatabase(settings.DatabaseName);

            _users = database.GetCollection<UserDto>(settings.CollectionName);
        }

        public async Task<bool> Register(UserDto userDto)
        {
            var filter = Builders<UserDto>.Filter.And(
            Builders<UserDto>.Filter.Eq(u => u.Email, userDto.Email),
            Builders<UserDto>.Filter.Eq(u => u.Password, userDto.Password)
        );

            var user = await _users.Find(filter).FirstOrDefaultAsync();

            if (user != null)
            {
                return false; // Registration failed
            }
            else { 

                userDto.Id = Guid.NewGuid();
                await _users.InsertOneAsync(userDto);
                return true;

            }
        }

        public async Task<UserDto?> Login(UserDto userDto)
        {
            var filter = Builders<UserDto>.Filter.And(
                Builders<UserDto>.Filter.Eq(u => u.Email, userDto.Email),
                Builders<UserDto>.Filter.Eq(u => u.Password, userDto.Password)
            );

            var user = await _users.Find(filter).FirstOrDefaultAsync();
            if(user != null)
            {
                return user;

            }
            return null;
        }

    }
}

export class PhoneService {

  private phones: Phone[]= [
    { id: 1, brand: 'Apple', model: 'iPhone 12', storage: 64, price: 799 },
    { id: 2, brand: 'Samsung', model: 'Galaxy S21', storage: 128, price: 699 },
    { id: 3, brand: 'Google', model: 'Pixel 5', storage: 128, price: 699 },
    { id: 4, brand: 'OnePlus', model: '9 Pro', storage: 256, price: 969 },
    { id: 5, brand: 'Xiaomi', model: 'Mi 11', storage: 256, price: 749 },
    { id: 6, brand: 'Motorola', model: 'Moto G Power (2021)', storage: 64, price: 199 },
    { id: 7, brand: 'Samsung', model: 'S22 Ultra', storage: 256, price: 969 },
    { id: 8, brand: 'Xiaomi', model: 'Mi 12', storage: 256, price: 749 },
    { id: 9, brand: 'Motorola', model: 'x', storage: 64, price: 199 },
  ];

  getPhones(): Observable<Phone[]> {
    // simulate delay of fetching data from API
    return of(this.phones).pipe(delay(1000));
  }

  addPhone(phone: Phone): void {
    phone.id = this.phones.length + 1;
    this.phones.push(phone);
  }

  updatePhone(phone: Phone): void {
    const index = this.phones.findIndex((p) => p.id === phone.id);
    if (index !== -1) {
      this.phones[index] = phone;
    }
  }

  deletePhone(phoneId: number): void {
    const index = this.phones.findIndex(p => p.id === phoneId);
    if (index !== -1) {
      this.phones.splice(index, 1);
    }
  }
}

    public class UsersController : ControllerBase
    {
        private readonly IUserService _userService;

        public UsersController(IUserService userService)
        {
            _userService = userService;
        }

        [HttpPost("register")]
        public async Task<IActionResult> Register(UserDto userDto)
        {
            var success = await _userService.Register(userDto);

            if (success)
            {
                return Ok(); // Registration successful
            }
            else
            {
                return BadRequest(); // Registration failed
            }
        }

        [HttpPost("login")]
        public async Task<IActionResult> Login([FromBody] UserDto userDto)
        {
            var success = await _userService.Login(userDto);
            if (success==null)
            {
                return NotFound("Announcement was not found");
            }

            return Ok(success); // Login successful
        }

    }
}

