class User {
  id: string
  phonesOwned: Phone[]
}

class Phone {
  id: string
  name: string
  ownedBy: User
}

class PhoneSwapRequest {
  id: string
  requesterId: string
  requestedPhone: Phone
  offeredPhone: Phone
  status: string
  comment: string

  +validateOwner(): boolean
}

class Frontend {
  +createModal(): void
  +handleFormSubmission(): void
}

class Backend {
  +createApiEndpoint(): void
  +storeSwapRequest(PhoneSwapRequest): void
  +updateStatus(PhoneSwapRequest, status): void
}

