            background: linear-gradient(to right, #4CAF50, #388E3C); /* Gradient green */
            color: white;
            padding: 14px 25px;
            margin: 10px 5px;
            border: none;
            border-radius: 8px;
            cursor: pointer;
            font-size: 16px;
            transition: transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out; /* Smoother transitions */
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
        }

        button:hover {
            transform: translateY(-2px);
            box-shadow: 0 6px 10px rgba(0, 0, 0, 0.2); /* Enhanced hover effect */
        }

        .modal {
            display: none;
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background-color: rgba(0, 0, 0, 0.5);
            z-index: 1000;
            overflow: auto; /* Enable scroll if content is long */
        }

        .modal-content {
            position: relative;
            background-color: #fff;
            margin: 10% auto; /* Adjust top margin to prevent content cutoff */
            padding: 20px;
            border-radius: 10px;
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
            width: 70%;
            max-width: 600px;
            animation: fadeIn 0.3s; /* Fade-in animation for modal */
        }

        @keyframes fadeIn {
            from { opacity: 0; }
            to { opacity: 1; }
        }

        .modal-content label, .modal-content select, .modal-content textarea {
            display: block;
            margin-bottom: 10px;
            width: 100%;
        }

        .modal-content select, .modal-content textarea {
            padding: 10px;
            border-radius: 6px;
            border: 1px solid #ddd;
            font-size: 16px;
            margin-top: 5px;
            box-sizing: border-box; /* Ensures padding doesn't affect width */
        }

        .close-button {
            position: absolute;
            top: 10px;
            right: 10px;
            font-size: 20px;
            cursor: pointer;
            color: #666;
        }

        #confirmation-message {
            display: none;
            margin-top: 20px;
            padding: 15px;
            background-color: #d4edda;
            color: #155724;
            border: 1px solid #c3e6cb;
            border-radius: 6px;
            font-weight: bold;
        }

        /* Enhanced phone list styling */
        #phone-list {
            list-style: none;
            padding: 0;
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(250px, 1fr)); /* Responsive grid layout */
            gap: 20px;
            margin-top: 20px;
        }

        .phone-item {
            background-color: #f9f9f9;
            border-radius: 10px;
            padding: 15px;
            box-shadow: 0 3px 7px rgba(0, 0, 0, 0.1);
            transition: transform 0.2s ease-in-out;
            text-align: left;
        }

        .phone-item:hover {
            transform: translateY(-3px);
        }

        .phone-item h3 {
            margin-top: 0;
            margin-bottom: 8px;
            color: #333;
        }

        .phone-item p {
            margin: 5px 0;
            color: #666;
        }
    </style>
</head>
<body>

<div class="container">
    <h1>Phone Panel</h1>
    <button onclick="openModal()">Request Phone Swap</button>

    <ul id="phone-list">
        <li class="phone-item">
            <h3>iPhone 14</h3>
            <p>Brand: Apple</p>
            <p>Price: $899</p>
            <p>Available: Yes</p>
        </li>
        <li class="phone-item">
            <h3>Samsung Galaxy S23</h3>
            <p>Brand: Samsung</p>
            <p>Price: $799</p>
            <p>Available: No</p>
        </li>
        <li class="phone-item">
            <h3>Google Pixel 7</h3>
            <p>Brand: Google</p>
            <p>Price: $699</p>
            <p>Available: Yes</p>
        </li>
        <!-- Add more phone items as needed -->
    </ul>

    <div id="confirmation-message">Request Submitted Successfully!</div>

    <div id="swapModal" class="modal">
        <div class="modal-content">
            <span class="close-button" onclick="closeModal()">&times;</span>
            <h2>Request Phone Swap</h2>
            <label for="phoneToRequest">Phone to Request:</label>
            <select id="phoneToRequest">
                <option value="iphone14">iPhone 14</option>
                <option value="pixel7">Google Pixel 7</option>
                <!-- Add other available phones -->
            </select>

            <label for="phoneToExchange">Phone to Exchange:</label>
            <select id="phoneToExchange">
                <option value="iphone12">iPhone 12 (Your Current Phone)</option>
                <!-- Add phones owned by the user -->
            </select>

            <label for="reason">Reason for Request (Optional):</label>
            <textarea id="reason" rows="4" cols="50"></textarea>

            <button onclick="submitRequest()">Submit Request</button>
            <button onclick="closeModal()">Cancel</button>
        </div>
    </div>
</div>

<script>
    function openModal() {
        document.getElementById('swapModal').style.display = 'block';
    }

    function closeModal() {
        document.getElementById('swapModal').style.display = 'none';
    }

    function submitRequest() {
        // Simulate request submission
        console.log('Request submitted');
        closeModal();
        document.getElementById('confirmation-message').style.display = 'block';

        // Hide the confirmation message after a few seconds
        setTimeout(function() {
            document.getElementById('confirmation-message').style.display = 'none';
        }, 3000);
    }

    // Close the modal if the user clicks outside of it
    window.onclick = function(event) {
        const modal = document.getElementById('swapModal');
        if (event.target == modal) {
            closeModal();
        }
    }
</script>

</body>
</html>
